function onScroll(i){var o=$(document).scrollTop();$(".steps--navigation li a").each(function(){var i=$(this),t=$(i.attr("href"));t.position().top<=o&&t.position().top+t.height()>o?($(".steps--navigation__link").removeClass("active"),i.addClass("active")):i.removeClass("active")}),$(".o2fixed__link span").each(function(){var i=$(this),t=$(i.attr("class"));t.position().top<=o&&t.position().top+t.height()>o?($(".o2fixed__link").removeClass("active"),i.parent().addClass("active"),$(this).parent().show()):(i.removeClass("active"),$(this).parent().hide())}),$(".o2fixed__link span").click(function(){$(".o2mobile").addClass("open").css("opacity",1)}),$(".o2mobile__link a").click(function(){$(".o2mobile").removeClass("open").css("opacity",0)})}